import React, { useState, useEffect } from "react";
import "./App.css";
import io from "socket.io-client";

const socket = io("http://localhost:5000");

const App = () => {
  const [config, setConfig] = useState({
    totalTickets: 0,
    releaseRate: 0,
    retrievalRate: 0,
    maxCapacity: 0,
  });

  const [ticketData, setTicketData] = useState({
    totalTickets: 0,
    maxTickets: 0,
    sellingTickets: 0,
    remainingTickets: 0,
  });

  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    // Fetch initial ticket data from backend
    fetch("http://localhost:5000/ticket-status")
      .then((res) => res.json())
      .then((data) => setTicketData(data))
      .catch((err) => console.error("Error fetching ticket data:", err));

    // Listen for real-time ticket updates
    socket.on("updateTicketData", (data) => {
      setTicketData(data);
    });

    return () => {
      socket.off("updateTicketData");
    };
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setConfig((prev) => ({ ...prev, [name]: parseInt(value, 10) || 0 }));
  };

  const handleSave = () => {
    fetch("http://localhost:5000/config", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(config),
    })
      .then((res) => res.json())
      .then((data) => {
        setTicketData(data.ticketData);
        alert("Configuration saved!");
      })
      .catch((err) => console.error("Error saving configuration:", err));
  };

  const handleClose = () => {
    setConfig({ totalTickets: 0, releaseRate: 0, retrievalRate: 0, maxCapacity: 0 });
    alert("Configuration cleared!");
  };

  const handleStart = () => {
    fetch("http://localhost:5000/start", { method: "POST" })
      .then((res) => res.json())
      .then(() => {
        setIsRunning(true);
        alert("Ticket selling started!");
      })
      .catch((err) => console.error("Error starting ticket selling:", err));
  };

  const handleStop = () => {
    fetch("http://localhost:5000/stop", { method: "POST" })
      .then((res) => res.json())
      .then(() => {
        setIsRunning(false);
        alert("Ticket selling stopped!");
      })
      .catch((err) => console.error("Error stopping ticket selling:", err));
  };

  const handleReset = () => {
    fetch("http://localhost:5000/reset", { method: "POST" })
      .then((res) => res.json())
      .then((data) => {
        setTicketData(data.ticketData);
        setIsRunning(false);
        alert("Ticket selling reset!");
      })
      .catch((err) => console.error("Error resetting ticket selling:", err));
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Ticket Management System</h1>
      </header>

      <div className="main-content">
        {/* Configuration Box */}
        <div className="box config-box">
          <h2>Configuration Details</h2>
          <form>
            <label>
              Total Number of Tickets:
              <input
                type="number"
                name="totalTickets"
                value={config.totalTickets}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Ticket Release Rate:
              <input
                type="number"
                name="releaseRate"
                value={config.releaseRate}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Customer Retrieval Rate:
              <input
                type="number"
                name="retrievalRate"
                value={config.retrievalRate}
                onChange={handleInputChange}
              />
            </label>
            <label>
              Maximum Ticket Capacity:
              <input
                type="number"
                name="maxCapacity"
                value={config.maxCapacity}
                onChange={handleInputChange}
              />
            </label>
            <div className="button-group">
              <button type="button" onClick={handleSave}>
                Save
              </button>
              <button type="button" onClick={handleClose}>
                Close
              </button>
            </div>
          </form>
        </div>

        {/* Ticket Pool Status */}
        <div className="box status-box">
          <h2>Ticket Pool Status</h2>
          <p>Available Tickets: {ticketData.remainingTickets}</p>
          <p>Release Rate: {config.releaseRate} tickets/sec</p>
          <p>Retrieval Rate: {config.retrievalRate} tickets/sec</p>
          <p>Max Capacity: {ticketData.maxTickets}</p>
        </div>

        {/* Control Panel */}
        <div className="box control-panel">
          <h2>Control Panel</h2>
          <button onClick={handleStart} disabled={isRunning}>
            Start
          </button>
          <button onClick={handleStop} disabled={!isRunning}>
            Stop
          </button>
          <button onClick={handleReset}>Reset</button>
        </div>
      </div>

      {/* Ticket Selling Details Box */}
      <div className="box selling-details">
        <h2>Real-Time Ticket Selling Details</h2>
        <p>Total Tickets: {ticketData.totalTickets}</p>
        <p>Maximum Tickets: {ticketData.maxTickets}</p>
        <p>Selling Tickets: {ticketData.sellingTickets}</p>
        <p>Remaining Tickets: {ticketData.remainingTickets}</p>
      </div>

      <footer className="footer">
        <p>&copy; 2024 Ticket Management System</p>
      </footer>
    </div>
  );
};

export default App;
