import React, { useState, useEffect } from "react";
import "./App.css";

const App = () => {
  const [configuration, setConfiguration] = useState({
    totalTickets: "",
    ticketReleaseRate: "",
    customerRetrievalRate: "",
    maxTicketCapacity: "",
  });

  const [savedConfig, setSavedConfig] = useState(null);
  const [ticketDetails, setTicketDetails] = useState({
    totalTickets: 0,
    maxTickets: 0,
    soldTickets: 0,
    remainingTickets: 0,
  });

  const [systemStatus, setSystemStatus] = useState("Stopped");

  const BASE_URL = "http://localhost:5001";

  const handleChange = (e) => {
    const { name, value } = e.target;
    setConfiguration({ ...configuration, [name]: value });
  };

  const handleConfigure = async () => {
    const { totalTickets, ticketReleaseRate, customerRetrievalRate, maxTicketCapacity } = configuration;

    if (!totalTickets || !ticketReleaseRate || !customerRetrievalRate || !maxTicketCapacity) {
      alert("Please fill out all fields!");
      return;
    }

    if (parseInt(maxTicketCapacity) > parseInt(totalTickets)) {
      alert("Max Ticket Capacity must be less than or equal to Total Tickets.");
      return;
    }

    try {
      const response = await fetch(`${BASE_URL}/api/configure`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(configuration),
      });

      const result = await response.json();

      if (response.ok) {
        setSavedConfig(configuration);
        setTicketDetails({
          totalTickets: parseInt(totalTickets),
          maxTickets: parseInt(maxTicketCapacity),
          soldTickets: 0,
          remainingTickets: parseInt(totalTickets),
        });
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error("Error configuring system:", error);
      alert("Failed to configure the system. Please try again.");
    }
  };

  const handleStart = async () => {
    if (!savedConfig) {
      alert("Please configure the system first!");
      return;
    }

    try {
      const response = await fetch(`${BASE_URL}/api/start`, {
        method: "POST",
      });

      const result = await response.json();

      if (response.ok) {
        setSystemStatus("Running");
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error("Error starting system:", error);
      alert("Failed to start the system. Please try again.");
    }
  };

  const handleStop = async () => {
    try {
      const response = await fetch(`${BASE_URL}/api/stop`, {
        method: "POST",
      });

      const result = await response.json();

      if (response.ok) {
        setSystemStatus("Stopped");
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error("Error stopping system:", error);
      alert("Failed to stop the system. Please try again.");
    }
  };

  const handleReset = () => {
    setConfiguration({
      totalTickets: "",
      ticketReleaseRate: "",
      customerRetrievalRate: "",
      maxTicketCapacity: "",
    });
    setSavedConfig(null);
    alert("Configuration form reset successfully!");
  };

  useEffect(() => {
    let intervalId;
    if (systemStatus === "Running") {
      intervalId = setInterval(async () => {
        try {
          const response = await fetch(`${BASE_URL}/api/status`);
          const result = await response.json();
          if (response.ok) {
            setTicketDetails({
              totalTickets: result.totalTickets,
              maxTickets: result.maxCapacity,
              soldTickets: result.ticketsSold,
              remainingTickets: result.remainingTickets,
            });
          }
        } catch (error) {
          console.error("Error fetching ticket details:", error);
        }
      }, 1000);
    }
    return () => clearInterval(intervalId);
  }, [systemStatus]);

  return (
    <div className="App">
      <header className="App-header">Welcome to Real-Time Ticketing System</header>

      <div className="App-body">
        {/* Box 1: Configuration Form */}
        <div className="box">
          <h2>Configuration Form</h2>
          <label>Total Tickets:</label>
          <input
            type="number"
            name="totalTickets"
            value={configuration.totalTickets}
            onChange={handleChange}
          />
          <label>Ticket Release Rate (seconds):</label>
          <input
            type="number"
            name="ticketReleaseRate"
            value={configuration.ticketReleaseRate}
            onChange={handleChange}
          />
          <label>Customer Retrieval Rate (seconds):</label>
          <input
            type="number"
            name="customerRetrievalRate"
            value={configuration.customerRetrievalRate}
            onChange={handleChange}
          />
          <label>Max Ticket Capacity:</label>
          <input
            type="number"
            name="maxTicketCapacity"
            value={configuration.maxTicketCapacity}
            onChange={handleChange}
          />
          <button onClick={handleConfigure}>Configure</button>
        </div>

        {/* Box 2: Ticket Pool Details */}
        <div className="box">
          <h2>Ticket Pool Details</h2>
          {savedConfig ? (
            <ul>
              <li>Total Tickets: {savedConfig.totalTickets}</li>
              <li>Ticket Release Rate: {savedConfig.ticketReleaseRate} seconds</li>
              <li>Customer Retrieval Rate: {savedConfig.customerRetrievalRate} seconds</li>
              <li>Max Ticket Capacity: {savedConfig.maxTicketCapacity}</li>
            </ul>
          ) : (
            <p>Please configure the system to see details.</p>
          )}
        </div>

        {/* Box 3: Control Panel */}
        <div className="box">
          <h2>Control Panel</h2>
          <button onClick={handleStart} disabled={systemStatus === "Running"}>
            Start
          </button>
          <button onClick={handleStop} disabled={systemStatus === "Stopped"}>
            Stop
          </button>
          <button onClick={handleReset}>Reset</button>
          <p>System Status: {systemStatus}</p>
        </div>

        {/* Box 4: Ticketing Details */}
        <div className="box">
          <h2>Ticketing Details</h2>
          <ul>
            <li>Total Tickets: {ticketDetails.totalTickets}</li>
            <li>Max Tickets: {ticketDetails.maxTickets}</li>
            <li>Selling Tickets: {ticketDetails.soldTickets}</li>
            <li>Remaining Tickets: {ticketDetails.remainingTickets}</li>
          </ul>
        </div>
      </div>

      {/* Footer */}
      <footer className="App-footer">
        <p>&copy; {new Date().getFullYear()} Real-Time Ticketing System. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
