import React, { useState, useEffect } from 'react';

const BASE_URL = 'http://localhost:5001'; // Backend API URL

function TicketingApp() {
  const [configuration, setConfiguration] = useState({
    totalTickets: '',
    ticketReleaseRate: '',
    customerRetrievalRate: '',
    maxTicketCapacity: '',
  });
  const [savedConfig, setSavedConfig] = useState(null);
  const [ticketDetails, setTicketDetails] = useState({
    totalTickets: 0,
    maxTickets: 0,
    soldTickets: 0,
    remainingTickets: 0,
  });
  const [systemStatus, setSystemStatus] = useState('Stopped');

  useEffect(() => {
    // Fetch current system status on page load
    if (savedConfig) {
      fetchTicketStatus();
    }
  }, [savedConfig]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setConfiguration({
      ...configuration,
      [name]: value,
    });
  };

  const handleConfigure = async () => {
    const totalTickets = parseInt(configuration.totalTickets, 10);
    const ticketReleaseRate = parseInt(configuration.ticketReleaseRate, 10);
    const customerRetrievalRate = parseInt(configuration.customerRetrievalRate, 10);
    const maxTicketCapacity = parseInt(configuration.maxTicketCapacity, 10);

    if (
      !totalTickets ||
      !ticketReleaseRate ||
      !customerRetrievalRate ||
      !maxTicketCapacity
    ) {
      alert("Please fill all fields with valid numbers!");
      return;
    }

    if (maxTicketCapacity <= 0 || maxTicketCapacity > totalTickets) {
      alert("Max Ticket Capacity must be greater than 0 and less than or equal to Total Tickets.");
      return;
    }

    try {
      const response = await fetch(`${BASE_URL}/api/configure`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          totalTickets,
          ticketReleaseRate,
          customerRetrievalRate,
          maxTicketCapacity,
        }),
      });

      const result = await response.json();

      if (response.ok) {
        setSavedConfig(configuration);
        setTicketDetails({
          totalTickets,
          maxTickets: maxTicketCapacity,
          soldTickets: 0,
          remainingTickets: totalTickets,
        });
        setSystemStatus('Stopped');
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error('Error configuring system:', error);
      alert('Failed to configure the system. Please try again.');
    }
  };

  const fetchTicketStatus = async () => {
    try {
      const response = await fetch(`${BASE_URL}/api/status`);
      const result = await response.json();
      if (response.ok) {
        setTicketDetails(result);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error('Error fetching ticket status:', error);
    }
  };

  const handleStartSystem = async () => {
    try {
      const response = await fetch(`${BASE_URL}/api/start`, {
        method: 'POST',
      });
      const result = await response.json();
      if (response.ok) {
        setSystemStatus('Running');
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error('Error starting system:', error);
    }
  };

  const handleStopSystem = async () => {
    try {
      const response = await fetch(`${BASE_URL}/api/stop`, {
        method: 'POST',
      });
      const result = await response.json();
      if (response.ok) {
        setSystemStatus('Stopped');
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error('Error stopping system:', error);
    }
  };

  const handleReleaseTicket = async () => {
    try {
      const response = await fetch(`${BASE_URL}/api/releaseTicket`, {
        method: 'POST',
      });
      const result = await response.json();
      if (response.ok) {
        setTicketDetails((prevState) => ({
          ...prevState,
          soldTickets: prevState.soldTickets + 1,
          remainingTickets: prevState.remainingTickets - 1,
        }));
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error('Error releasing ticket:', error);
    }
  };

  const handleRetrieveTicket = async () => {
    try {
      const response = await fetch(`${BASE_URL}/api/retrieveTicket`, {
        method: 'POST',
      });
      const result = await response.json();
      if (response.ok) {
        setTicketDetails((prevState) => ({
          ...prevState,
          soldTickets: prevState.soldTickets - 1,
          remainingTickets: prevState.remainingTickets + 1,
        }));
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error('Error retrieving ticket:', error);
    }
  };

  const handleResetSystem = async () => {
    try {
      const response = await fetch(`${BASE_URL}/api/reset`, {
        method: 'POST',
      });
      const result = await response.json();
      if (response.ok) {
        setSavedConfig(null);
        setTicketDetails({
          totalTickets: 0,
          maxTickets: 0,
          soldTickets: 0,
          remainingTickets: 0,
        });
        setSystemStatus('Stopped');
        alert(result.message);
      } else {
        alert(result.message);
      }
    } catch (error) {
      console.error('Error resetting system:', error);
    }
  };

  return (
    <div>
      <h1>Ticketing System</h1>

      {!savedConfig && (
        <div>
          <h2>Configure System</h2>
          <form onSubmit={(e) => e.preventDefault()}>
            <div>
              <label>Total Tickets: </label>
              <input
                type="number"
                name="totalTickets"
                value={configuration.totalTickets}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Ticket Release Rate: </label>
              <input
                type="number"
                name="ticketReleaseRate"
                value={configuration.ticketReleaseRate}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Customer Retrieval Rate: </label>
              <input
                type="number"
                name="customerRetrievalRate"
                value={configuration.customerRetrievalRate}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Max Ticket Capacity: </label>
              <input
                type="number"
                name="maxTicketCapacity"
                value={configuration.maxTicketCapacity}
                onChange={handleChange}
              />
            </div>
            <button onClick={handleConfigure}>Configure System</button>
          </form>
        </div>
      )}

      {savedConfig && (
        <div>
          <h2>System Status</h2>
          <p>Status: {systemStatus}</p>
          <p>Total Tickets: {ticketDetails.totalTickets}</p>
          <p>Max Capacity: {ticketDetails.maxTickets}</p>
          <p>Tickets Sold: {ticketDetails.soldTickets}</p>
          <p>Remaining Tickets: {ticketDetails.remainingTickets}</p>

          <div>
            {systemStatus === 'Stopped' ? (
              <button onClick={handleStartSystem}>Start System</button>
            ) : (
              <button onClick={handleStopSystem}>Stop System</button>
            )}

            {systemStatus === 'Running' && (
              <div>
                <button onClick={handleReleaseTicket}>Release Ticket</button>
                <button onClick={handleRetrieveTicket}>Retrieve Ticket</button>
              </div>
            )}

            <button onClick={handleResetSystem}>Reset System</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default TicketingApp;
